            MD51Key





:

        private static Data4Net.DKey.DKeyMd5 _dkMd5 = new Data4Net.DKey.DKeyMd5("0123456789", 6,

            "aaa" +

            "{T}" +

            "bbb" +

            "{V}{T}" +

            "ccc",

            60000);

        public static bool CheckCodeByMd5(string value, string code)

        {

            string checkKey = "";

            bool r = _dkMd5.CheckKey(value, code, 10, out checkKey);

            LogHelper.Info("CheckCodeByMd5 [VALUE=" + value + "][CODE=" + code + "][R=" + r.ToString() + "][KEY=" + checkKey + "]");

            return r;

        }

        public static string GetCodeByMd5(string value)

        {

            string r = _dkMd5.GetKey(value);

            LogHelper.Info("CheckCodeByMd5 [VALUE=" + value + "][NOW=" + DateTime.Now.ToString() + "][MD5=" + r + "]");

            return r;

        }

