            PostClient SubmitImage





        private static void PostImage(string urls, byte[] imgSave)

        {

            string imgName = DateTime.Now.ToString("yyyy-MM") + "\\"

                + DateTime.Now.ToString("yyyyMMddHHmmssfff")

                + RandomHelper.GetRndStr("0123456789", 4) + ".jpg"; //

            string dataPost = "img={IMG}"

                    + "&imgname="

                    + HttpUtility.UrlEncode(imgName, Encoding.GetEncoding("utf-8"))

                    + "&key="

                    + Utility.GetCodeByMd5(imgName)

                    + "&time="

                    + HttpUtility.UrlEncode(DateTime.Now.ToString("yyyyMMddHHmmss"), Encoding.GetEncoding("utf-8"));



            string error = "", result = "";

            bool r = ImageHelper.PostClientSubmitImage(urls, imgSave, System.Drawing.Imaging.ImageFormat.Jpeg,

                    GetAddInfo(), dataPost, 20000,

                    "User-Agent: Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.2; .NET CLR 1.0.3705;)", "utf-8",

                    "^$^", Environment.NewLine, out result, out error);

            if (!string.IsNullOrEmpty(error))

            {

                LogHelper.WarnMail("PostImage [EX]" + error);

            }

            LogHelper.Info("PostImage [R=" + r + "][RESULT=" + result + "][URL=" + urls + "]");

        }

